const ranks = document.getElementById('ranks')
const hotMovies = document.getElementById("hot_movies")
const onlineMovies = document.getElementById("online_movies")
const recommends = document.getElementById('recommends')

function getData(){
    const modules = JSON.parse(fakeJson)['modules']
    for(let module of modules){
        const moduleName = module['module_name'] 
        if(moduleName === 'movie_hot_gaia'){
            parseHotMovies(module['data']['subject_collection_boards'][0]['items'])
        } else if(moduleName === 'movie_selected_collections'){
            parseRanks(module['data']['selected_collections'])
        } else if(moduleName === 'movie_showing'){
            parseOnlines(module['data']['subject_collection_boards'][0]['items'])
        }
    }
    parseRecommends()
}

function addHotMovieItem(cover, title, rating){
    const hotMovieElement = document.createElement("div")
    hotMovieElement.setAttribute('class', 'hot-movie-item')
    hotMovies.appendChild(hotMovieElement)
    
    //cover
    const coverElement = document.createElement('div')
    coverElement.setAttribute('class', 'hot-movie-item-cover')
    coverElement.style.backgroundImage = `url("${cover}")`
    hotMovieElement.appendChild(coverElement)

    //title
    const titleElement = document.createElement('div')
    titleElement.setAttribute('class', 'hot-movie-item-title')
    titleElement.innerHTML = title
    hotMovieElement.appendChild(titleElement)

    //rating
    const ratingElement = document.createElement('div')
    ratingElement.setAttribute('class', 'rating-container')
    //star
    const stars = rating / 2
    for(let i = 1; i <= 5; i++){
        const starElement = document.createElement('div')
        starElement.setAttribute('class', i <= stars ? 'rating rating-full' : 'rating rating-empty')
        ratingElement.appendChild(starElement)
    }
    //score
    const ratingTextElement = document.createElement('div')
    ratingTextElement.setAttribute('class', 'rating-text')
    ratingTextElement.innerHTML = `${rating}`
    ratingElement.appendChild(ratingTextElement)

    hotMovieElement.appendChild(ratingElement)
}

function parseHotMovies(movieItems){
    for(let movieItem of movieItems){
        addHotMovieItem(movieItem['cover']['url'], movieItem['title'], movieItem['rating']['value'])
    }
}

function parseRanks(rankItems){
    for(let rankItem of rankItems){
        const rankItemElement = document.createElement('div')
        rankItemElement.setAttribute('class', 'rank-item')
        rankItemElement.style.backgroundColor = `#${rankItem['background_color_scheme']['primary_color_dark']}`
        ranks.appendChild(rankItemElement)

        //left
        const rankItemLeft = document.createElement('div')
        rankItemLeft.setAttribute('class', 'rank-item-left')
        rankItemElement.appendChild(rankItemLeft)

        if(rankItem['icon_fg_image'] == null){
            //left-hint
            const rankItemLeftHint = document.createElement('div')
            rankItemLeftHint.setAttribute('class', 'rank-item-left-hint')
            rankItemLeftHint.innerHTML = rankItem['type_text']
            rankItemLeft.appendChild(rankItemLeftHint)

            //left-title
            const rankItemLeftTitle = document.createElement('div')
            rankItemLeftTitle.setAttribute('class', 'rank-item-left-title')
            rankItemLeftTitle.innerHTML = rankItem['medium_name']
            rankItemLeft.appendChild(rankItemLeftTitle)
        } else {
            rankItemLeft.style.backgroundImage = `url("${rankItem['icon_fg_image']}")`
            rankItemLeft.style.backgroundSize = '80%'
            rankItemLeft.style.backgroundRepeat = 'no-repeat'
            rankItemLeft.style.backgroundPosition = 'center center'
        }

        //right
        const rankItemRight = document.createElement('div')
        rankItemRight.setAttribute('class', 'rank-item-right')
        rankItemRight.style.backgroundImage = `url("${rankItem['header_bg_image']}")`
        rankItemElement.appendChild(rankItemRight)

        //item list
        const rankListElement = document.createElement('div')
        rankListElement.setAttribute('class', 'rank-item-list')
        rankItemRight.appendChild(rankListElement)

        //specific items
        for(let i = 0; i < rankItem['items'].length; i++){
            let movieItem = rankItem['items'][i]
            const rankItemInfoElement = document.createElement('div')
            rankItemInfoElement.setAttribute('class', 'rank-item-info')
            rankItemInfoElement.innerHTML = `${i + 1}. ${movieItem['title']} <span>${movieItem['rating']['value']}</span>`
            rankListElement.appendChild(rankItemInfoElement)
        }
    }
}

function parseOnlines(onlineItems){
    const onlineMovies = document.getElementById('online_movies')
    const end = onlineItems.length > 6 ? 6 : onlineItems.length
    for(let i = 0; i < end; i++){
        const movieItem = onlineItems[i]
        addOnlineMovieItem(movieItem['cover']['url'], movieItem['title'], movieItem['rating']['value'])
    }
}

function addOnlineMovieItem(cover, title, rating){
    const hotMovieElement = document.createElement("div")
    hotMovieElement.setAttribute('class', 'hot-movie-item')
    hotMovieElement.style.width = '130px'
    hotMovieElement.style.marginLeft = 0
    onlineMovies.appendChild(hotMovieElement)
    
    //cover
    const coverElement = document.createElement('div')
    coverElement.setAttribute('class', 'hot-movie-item-cover')
    coverElement.style.backgroundImage = `url("${cover}")`
    coverElement.style.height = '150px'
    hotMovieElement.appendChild(coverElement)

    //title
    const titleElement = document.createElement('div')
    titleElement.setAttribute('class', 'hot-movie-item-title')
    titleElement.innerHTML = title
    hotMovieElement.appendChild(titleElement)

    //rating
    const ratingElement = document.createElement('div')
    ratingElement.setAttribute('class', 'rating-container')
    //star
    const stars = rating / 2
    for(let i = 1; i <= 5; i++){
        const starElement = document.createElement('div')
        starElement.setAttribute('class', i <= stars ? 'rating rating-full' : 'rating rating-empty')
        ratingElement.appendChild(starElement)
    }
    //score
    const ratingTextElement = document.createElement('div')
    ratingTextElement.setAttribute('class', 'rating-text')
    ratingTextElement.innerHTML = `${rating}`
    ratingElement.appendChild(ratingTextElement)

    hotMovieElement.appendChild(ratingElement)
}

function parseRecommends(){
    console.log(recommends)
    for(let recommend of JSON.parse(fakeRecommends)['items']){
        if(recommend['pic'] == undefined)
            continue
            
        const recommendItemElement = document.createElement('div')
        recommendItemElement.setAttribute('class', 'recommend-item')
        recommends.appendChild(recommendItemElement)

        //left
        const recommendCover = document.createElement('div')
        recommendCover.setAttribute('class', 'recommend-item-left')
        recommendCover.style.backgroundImage = `url("${recommend['pic']['normal']}")`
        recommendItemElement.appendChild(recommendCover)

        //right
        const recommendRight = document.createElement('div')
        recommendRight.setAttribute('class', 'recommend-item-right')
        recommendItemElement.appendChild(recommendRight)

        //right-title
        const recommendTitle = document.createElement('div')
        recommendTitle.setAttribute('class', 'recommend-item-title')
        recommendTitle.innerHTML = `${recommend.title} <span>(${recommend['year']})</span>`
        recommendRight.appendChild(recommendTitle)

        //rating
        const ratingElement = document.createElement('div')
        ratingElement.setAttribute('class', 'rating-container')
        recommendRight.appendChild(ratingElement)
        //star
        const stars = recommend['rating']['value'] / 2
        for(let i = 1; i <= 5; i++){
            const starElement = document.createElement('div')
            starElement.setAttribute('class', i <= stars ? 'rating rating-full' : 'rating rating-empty')
            ratingElement.appendChild(starElement)
        }
        //score
        const ratingTextElement = document.createElement('div')
        ratingTextElement.setAttribute('class', 'rating-text')
        ratingTextElement.innerHTML = `${recommend['rating']['value']}`
        ratingElement.appendChild(ratingTextElement)

        //info
        const recommendInfo = document.createElement('p')
        recommendInfo.setAttribute('class', 'recommend-item-info')
        recommendInfo.innerHTML = recommend['card_subtitle']
        recommendRight.appendChild(recommendInfo)

        //reason
        const recommendReason = document.createElement('div')
        recommendReason.setAttribute('class', 'recommend-item-reason')
        recommendReason.innerHTML = recommend['recommended_reason']
        recommendRight.appendChild(recommendReason)
    }
}

